// Extraer la pregunta del input
const question = $input.item.json.question || $input.item.json.query || '';
const lowerQuestion = question.toLowerCase();

// Detectar idioma (español por defecto)
const isEnglish = /\b(what|how|can|do|you|services|contact|about|portfolio)\b/i.test(question) && 
                  !/\b(qué|cómo|servicios|contacto|sobre|portafolio)\b/i.test(question);

return {
  json: {
    question: question,
    lowerQuestion: lowerQuestion,
    language: isEnglish ? 'en' : 'es',
    timestamp: new Date().toISOString()
  }
};

// Formatear respuesta para el agente principal
const response = $input.item.json.response;
const category = $input.item.json.category;
const confidence = $input.item.json.confidence;
const language = $input.item.json.language;

return {
  json: {
    output: response,
    success: true,
    source: 'knowledge_base',
    category: category,
    confidence: confidence,
    language: language
  }
};
