// Extraer datos del ticket
const ticketData = $input.item.json.ticketData || $input.item.json.query || '';

// Parsear información usando regex mejorado
const nameMatch = ticketData.match(/(?:nombre?|name)[:\s]+([^,\n]+)/i);
const emailMatch = ticketData.match(/(?:email|correo)[:\s]+([^\s,\n]+)/i);
const descMatch = ticketData.match(/(?:descripci[oó]n?|description)[:\s]+(.+)/is);
const companyMatch = ticketData.match(/(?:empresa|company)[:\s]+([^,\n]+)/i);

const name = nameMatch ? nameMatch[1].trim() : 'No proporcionado';
const email = emailMatch ? emailMatch[1].trim() : 'No proporcionado';
const description = descMatch ? descMatch[1].trim() : ticketData;
const company = companyMatch ? companyMatch[1].trim() : 'No especificado';

return {
  json: {
    name: name,
    email: email,
    description: description,
    company: company,
    ticketId: `AURIN-${Date.now()}`,
    createdAt: new Date().toISOString()
  }
};

// Formatear respuesta de éxito para el usuario
const ticketData = $('Parse Ticket Data').item.json;

return {
  json: {
    output: `✅ **Perfecto, ${ticketData.name}!**

He creado un ticket de soporte y nuestro equipo te contactará a **${ticketData.email}** muy pronto.

**📋 Detalles de tu ticket:**
- Número de ticket: **${ticketData.ticketId}**
- Estado: En proceso
- Tiempo estimado de respuesta: 24 horas

**📧 Email de contacto:** [hey@aurin.mx](mailto:hey@aurin.mx)

Nos pondremos en contacto contigo dentro de las próximas 24 horas para atender tu solicitud personalmente.

¿Hay algo más en lo que pueda ayudarte mientras tanto?`,
    success: true,
    ticketId: ticketData.ticketId,
    emailSent: true,
    customerName: ticketData.name,
    customerEmail: ticketData.email
  }
};
