// === PARSEAR SOLICITUD v3 ===
const query = ($input.item.json.query || '').toLowerCase();
const customerEmail = $input.item.json.customerEmail || '';
const pendingBookingIn = $input.item.json.pendingBooking || null;
const customerData = $input.item.json.customerData || null;

// Acciones
const isBooking = /(agendar|reservar|programar|book|cita)/i.test(query);
const isCancel = /(cancelar|eliminar|quitar)/i.test(query);
const isCheck = /(disponible|horario|cuando|available)/i.test(query);
const isConfirm = /(confirmar|confirmo|si confirmo|confirm|yes)/i.test(query);

let action = 'check';
if (isBooking) action = 'book';
if (isCancel) action = 'cancel';
if (isConfirm) action = 'confirm';

// Detectar si el usuario estÃ¡ respondiendo a la peticiÃ³n de datos para agendar
if (pendingBookingIn && customerData && !isCancel && !isConfirm) {
  action = 'create_event';
}

// Extraer dÃ­a y hora (solo para 'book')
let targetDate = null;
let targetTime = null;
if (action === 'book') {
  const daysEs = ['lunes','martes','miercoles','jueves','viernes','sabado','domingo'];
  const daysEn = ['monday','tuesday','wednesday','thursday','friday','saturday','sunday'];
  for (let i = 0; i < 7; i++) {
    const d = new Date();
    const diff = (i + 7 - d.getDay() + 1) % 7 || 7;
    d.setDate(d.getDate() + diff);
    if (query.includes(daysEs[i]) || query.includes(daysEn[i])) {
      targetDate = d.toISOString().split('T')[0];
      break;
    }
  }
  const timeMatch = query.match(/(\d{1,2})(?::(\d{2}))?\s?(am|pm)?/i);
  if (timeMatch) {
    let [_, h, m, p] = timeMatch;
    let hours = parseInt(h);
    const minutes = m || '00';
    if (p) {
      if (p.toLowerCase() === 'pm' && hours !== 12) hours += 12;
      if (p.toLowerCase() === 'am' && hours === 12) hours = 0;
    }
    targetTime = `${hours.toString().padStart(2,'0')}:${minutes}`;
  }
}

return [{
  json: {
    query,
    action,
    targetDate,
    targetTime,
    customerEmail,
    customerData,
    pendingBookingIn
  }
}];

// === PROCESAR ACCIÃ“N v3 ===
const { action, targetDate, targetTime, customerEmail, customerData, pendingBookingIn } = $json;
const events = $input.all().filter(i => i.json.id).map(i => i.json);

const allowedTimes = ['09:00','10:00','11:00','14:00','15:00','16:00','17:00'];

let response = '';
let needsData = false;
let pendingBooking = null;
let cancelEventId = null;
let pendingUpdate = null;
let shouldCreateEvent = false;
let shouldDeleteEvent = false;
let shouldUpdateEvent = false;

if (action === 'check') {
  response = `ðŸ“… **Disponibilidad esta semana (CDMX, GMT-6)**\n\n`;
  const days = ['Domingo','Lunes','Martes','MiÃ©rcoles','Jueves','Viernes','SÃ¡bado'];
  for (let i = 0; i < 7; i++) {
    const d = new Date();
    d.setDate(d.getDate() + i);
    if (d.getDay() === 0 || d.getDay() === 6) continue;
    const dateStr = d.toISOString().split('T')[0];
    const dayEvents = events.filter(e => e.start?.dateTime?.startsWith(dateStr));
    const busy = dayEvents.map(e => new Date(e.start.dateTime).toLocaleTimeString('es-MX',{hour:'2-digit',minute:'2-digit',hour12:false}));
    const free = allowedTimes.filter(t => !busy.includes(t));
    if (free.length > 0) {
      response += `**${days[d.getDay()]}**: ${free.join(', ')}\n`;
    }
  }
  response += `\nÂ¿QuÃ© dÃ­a y hora prefieres? (Ej: "Jueves a las 3 PM")`;

} else if (action === 'book') {
  if (!targetDate || !targetTime || !allowedTimes.includes(targetTime)) {
    response = `â° Necesito un horario vÃ¡lido. Ejemplo: "Martes a las 10 AM"\n\nHorarios disponibles: 9-11 AM y 2-5 PM`;
  } else {
    const startISO = `${targetDate}T${targetTime}:00-06:00`;
    const endISO = new Date(new Date(startISO).getTime() + 45*60*1000).toISOString();
    const conflict = events.some(e => {
      const s1 = new Date(e.start.dateTime); const e1 = new Date(e.end.dateTime);
      const s2 = new Date(startISO); const e2 = new Date(endISO);
      return s1 < e2 && s2 < e1;
    });
    if (conflict) {
      response = `âŒ Lo siento, ese horario ya estÃ¡ ocupado. Â¿Otro horario?`;
    } else {
      response = `âœ… Â¡Perfecto! Confirmemos tu cita:\n\nðŸ“… **${new Date(startISO).toLocaleString('es-MX',{weekday:'long',month:'long',day:'numeric',hour:'numeric',minute:'numeric'})}**\nâ±ï¸ DuraciÃ³n: 45 min\n\nPara confirmar, necesito:\n1. Nombre completo\n2. Email\n3. Empresa (opcional)\n4. Â¿De quÃ© trata tu proyecto?\n\n_Responde con esta info y agendarÃ© tu cita._`;
      needsData = true;
      pendingBooking = { start: startISO, end: endISO };
    }
  }

} else if (action === 'create_event') {
  response = `Creando evento...`;
  shouldCreateEvent = true;

} else if (action === 'cancel') {
  if (!customerEmail) {
    response = `Para cancelar, necesito el email con el que agendaste.`;
  } else {
    const pending = events.find(e => 
      e.attendees?.some(a => a.email === customerEmail) && 
      !e.summary.includes('[CANCELADO]')
    );
    if (pending) {
      cancelEventId = pending.id;
      shouldDeleteEvent = true;
      response = `Cancelando tu cita...`;
    } else {
      response = `No encontrÃ© ninguna cita pendiente con tu email **${customerEmail}**.`;
    }
  }

} else if (action === 'confirm') {
  if (!customerEmail) {
    response = `Para confirmar, necesito el email con el que agendaste.`;
  } else {
    const eventToConfirm = events.find(e => 
      e.attendees?.some(a => a.email === customerEmail) && 
      e.summary.includes('[PENDIENTE CONFIRMACIÃ“N]')
    );
    if (eventToConfirm) {
      pendingUpdate = {
        id: eventToConfirm.id,
        summary: eventToConfirm.summary.replace('[PENDIENTE CONFIRMACIÃ“N]', '[CONFIRMADA]'),
        description: (eventToConfirm.description || '').replace('Pendiente de confirmaciÃ³n (24h).', 'Cita confirmada por el usuario.')
      };
      shouldUpdateEvent = true;
      response = `Confirmando cita...`;
    } else {
      response = `No encontrÃ© ninguna cita pendiente de confirmaciÃ³n con tu email. Â¿QuizÃ¡s ya la habÃ­as confirmado?`;
    }
  }
}

return [{
  json: {
    output: response,
    needsData,
    pendingBooking,
    cancelEventId,
    pendingUpdate,
    action,
    customerData: $json.customerData,
    pendingBookingIn: $json.pendingBookingIn,
    shouldCreateEvent,
    shouldDeleteEvent,
    shouldUpdateEvent
  }
}];

const event = $('Create Event').item.json;
const customerData = $('Process Action').item.json.customerData;
const pendingBookingIn = $('Process Action').item.json.pendingBookingIn;

if (!event || !event.hangoutLink) {
  return [{ json: { output: 'Error al crear evento.' } }];
}

return [{
  json: {
    output: `Â¡Cita agendada!\n\n**${new Date(event.start.dateTime).toLocaleString('es-MX')}**\nðŸ”— Enlace Meet: ${event.hangoutLink}\nðŸ“… Ver en calendario: ${event.htmlLink}\n\nTe enviamos un email de confirmaciÃ³n. \n**IMPORTANTE: Responde 'CONFIRMAR' en el chat en las prÃ³ximas 24h o la cita se cancelarÃ¡.**\n\nÂ¿Algo mÃ¡s?`,
    eventId: event.id,
    meetLink: event.hangoutLink,
    calendarLink: event.htmlLink,
    requiresConfirmation: true,
    customerEmail: customerData.email,
    pendingBooking: null
  }
}];

return [{ json: { 
  output: `Cita cancelada exitosamente.\n\nÂ¿Deseas agendar una nueva?`,
  customerEmail: null,
  pendingBooking: null
} }];

const event = $('Update Event (Confirm)').item.json;
const customerEmail = $('Process Action').item.json.customerEmail;

return [{
  json: {
    output: `âœ… **Â¡Cita confirmada!**\n\nTu cita para el **${new Date(event.start.dateTime).toLocaleString('es-MX')}** estÃ¡ 100% asegurada.\n\nðŸ”— Enlace Meet: ${event.hangoutLink}\nðŸ“… Ver en calendario: ${event.htmlLink}\n\nÂ¡Nos vemos pronto!\n\nÂ¿Algo mÃ¡s en lo que te pueda ayudar?`,
    success: true,
    eventConfirmed: true,
    customerEmail: customerEmail,
    pendingBooking: null
  }
}];
