// Parse Ticket Data - Extrae datos del JSON del agente

const ticketDataRaw = $input.item.json.ticketData || $input.item.json.query || '';
let fileUrl = $input.item.json.fileUrl || null;

console.log('=== RAW INPUT ===');
console.log('ticketDataRaw type:', typeof ticketDataRaw);
console.log('ticketDataRaw length:', ticketDataRaw.length);
console.log('ticketDataRaw value:', ticketDataRaw);
console.log('ticketDataRaw first 50 chars:', ticketDataRaw.substring(0, 50));
console.log('fileUrl from input:', fileUrl);

let parsed;
try {
  console.log('Attempting JSON.parse...');
  parsed = JSON.parse(ticketDataRaw);
  console.log('‚úÖ JSON parsed successfully');
  console.log('Parsed object keys:', Object.keys(parsed));
  console.log('Parsed object:', parsed);
} catch (e) {
  console.error('‚ùå JSON parse failed:', e.message);
  return {
    json: {
      name: 'Error parsing',
      email: 'Error parsing',
      company: 'No especificado',
      service: 'No especificado',
      subject: 'Error parsing ticket',
      description: ticketDataRaw,
      ticketId: `AURIN-${Date.now()}`,
      createdAt: new Date().toISOString(),
      fileUrl: null
    }
  };
}

// Extraer campos con nullish coalescing
const name = parsed.nombre_completo ?? parsed.nombre ?? parsed.name ?? 'No proporcionado';
const email = parsed.email ?? parsed.correo ?? 'No proporcionado';

// Validaci√≥n segura con type checking antes de .trim()
let company = parsed.empresa ?? parsed.company ?? '';
if (!company || company === 'null' || (typeof company === 'string' && company.trim() === '')) {
  company = 'No especificado';
}

let service = parsed.servicio_especifico ?? parsed.servicio ?? parsed.service ?? '';
if (!service || service === 'null' || (typeof service === 'string' && service.trim() === '')) {
  service = 'No especificado';
}

let subject = parsed.asunto ?? parsed.subject ?? '';
if (!subject || subject === 'null' || (typeof subject === 'string' && subject.trim() === '')) {
  subject = 'Consulta general';
}

const description = parsed.descripcion ?? parsed.description ?? 'Sin descripci√≥n';

// Archivo adjunto: buscar en m√∫ltiples campos con validaci√≥n segura
const jsonFileUrl = parsed.archivo_adjunto ?? parsed.archivoAdjunto ?? parsed.fileUrl ?? '';
if (jsonFileUrl && typeof jsonFileUrl === 'string' && jsonFileUrl !== 'null' && jsonFileUrl.trim() !== '' && jsonFileUrl.startsWith('http')) {
  fileUrl = jsonFileUrl.trim();
  console.log('‚úÖ FileUrl extra√≠do del JSON:', fileUrl);
} else {
  console.log('‚ÑπÔ∏è No fileUrl en JSON, usando fileUrl del input:', fileUrl);
}

const ticketId = `AURIN-${Date.now()}`;
const createdAt = new Date().toISOString();

console.log('=== EXTRACTED ===');
console.log('Name:', name);
console.log('Email:', email);
console.log('Company:', company);
console.log('Service:', service);
console.log('Subject:', subject);
console.log('Description:', description);
console.log('FileUrl FINAL:', fileUrl);

return {
  json: {
    name,
    email,
    company,
    service,
    subject,
    description,
    ticketId,
    createdAt,
    fileUrl
  }
};

const apiResponse = $input.item.json;

if (apiResponse.output) {
  return {
    json: {
      output: apiResponse.output,
      success: apiResponse.success,
      ticketId: apiResponse.ticketId,
      emailSent: apiResponse.emailSent
    }
  };
}

const ticketData = $('Parse Ticket Data').item.json;
const hasFile = ticketData.fileUrl ? ' Tu archivo adjunto ha sido incluido.' : '';
const serviceInfo = ticketData.service && ticketData.service !== 'No especificado' ? `\n- Servicio: **${ticketData.service}**` : '';

return {
  json: {
    output: `‚úÖ **Perfecto, ${ticketData.name}!**\n\nHe creado un ticket de soporte y nuestro equipo te contactar√° a **${ticketData.email}** muy pronto.${hasFile}\n\n**üìã Detalles de tu ticket:**\n- N√∫mero de ticket: **${ticketData.ticketId}**\n- Asunto: **${ticketData.subject}**${serviceInfo}\n- Estado: En proceso\n- Tiempo estimado de respuesta: 24 horas\n\n**üìß Email de contacto:** [hey@aurin.mx](mailto:hey@aurin.mx)\n\nNos pondremos en contacto contigo dentro de las pr√≥ximas 24 horas para atender tu solicitud personalmente.\n\n¬øHay algo m√°s en lo que pueda ayudarte mientras tanto?`,
    success: true,
    ticketId: ticketData.ticketId,
    emailSent: true,
    customerName: ticketData.name,
    customerEmail: ticketData.email,
    customerService: ticketData.service,
    customerSubject: ticketData.subject,
    fileAttached: !!ticketData.fileUrl
  }
};
